mapper.select:
select
id, name, startDate, startTime, endDate, endTime, address, cost, discount, status, remark, created_time,
updated_time, updated_person
from studentmanage.activity


        and id = #{id}


        and name = #{name}


        and startDate = #{startdate}


        and startTime = #{starttime}


        and endDate = #{enddate}


        and endTime = #{endtime}


        and address = #{address}


        and cost = #{cost}


        and discount = #{discount}


        and status = #{status}


        and remark = #{remark}


        and created_time = #{createdTime}


        and updated_time = #{updatedTime}


        and updated_person = #{updatedPerson}

select
id, name, startDate, startTime, endDate, endTime, address, cost, discount, status, remark, created_time, updated_time, updated_person
from studentmanage.activity


        and id = #{activity.id}


        and name = #{activity.name}


        and startDate = #{activity.startdate}


        and startTime = #{activity.starttime}


        and endDate = #{activity.enddate}


        and endTime = #{activity.endtime}


        and status = #{activity.status}


        and updated_person = #{activity.updatedPerson}


order by updated_time desc
limit #{offset}, #{limit}

select count(1)
from studentmanage.activity


        and name = #{student.name}


        and nickname = #{student.nickname}


        and gender = #{student.gender}


        and status = #{student.status}
